import React , {useState , useEffect} from 'react'
import { Link } from 'react-router-dom'
import service from '../../config/axios'

export default function Store() {

    const [product, setProduct] = useState()

    useEffect(async()=>{

          const productDB = await service.get("http://localhost:3001/product/all")
          setProduct(productDB.data)

      },[])


    return (
        <>
    {
        product === undefined?(<img style={{flex:'display', justifyContent:'center', alignItems:'center'  }} style={{width:70}} src="https://res.cloudinary.com/dannblackfish/image/upload/v1615395373/deadstarclothing/gif/Logo-DS_a7t7bj.gif"/>):(

        product.map((e,id)=>{
        return(
            <>
            <div class="bg-white">
                    <div class="mx-auto py-12 px-4 max-w-7xl sm:px-6 lg:px-8 lg:py-20">
                        <div class="space-y-12" class="space-y-12 sm:grid sm:grid-cols-2 sm:gap-x-6 sm:gap-y-12 sm:space-y-0 lg:grid-cols-3 lg:gap-x-8">
                        <Link to={`/product/${e._id}`}>
                            <li key={id}>
                            <div class="space-y-4">
                                <div class="">
                                <img class="object-cover shadow-lg rounded-lg" src={e.image[0]} alt="" />
                                </div>
                                <div class="space-y-2">
                                <div class="text-lg leading-6 font-medium space-y-1">
                                    <h3 class="text-center text-3xl text-pink-300">{e.name}</h3>
                                    <p class="text-pink-400 text-center text-xl">${e.price}</p>
                                </div>
                                </div>
                            </div>
                            </li>
                            </Link>

                        </div>
                    </div>
            </div>
        </>
        )
        })
        )
    }
    </>
    )
}
