import React, { useEffect, useState, useContext } from 'react'
import { useParams } from 'react-router-dom'
import service from '../../config/axios'



export default function CategoryProduct() {

    // const [product, setProduct] = useState()

    // useEffect(async()=>{

    //       const productDB = await service.get("http://localhost:3001/product/all")
    //       setProduct(productDB.data)

    //   },[])


    // const idProduct = useParams().id

    // const [productDetail, setProductDetail] = useState()

    // const ctxauth = useContext(AuthContext)
    // const {usuario, autenticado, agregarCarrito} = ctxauth



    // useEffect(async() => {

    //     const detail = await service.get(`http://localhost:3001/product/detail/${idProduct}`)
    //     console.log("este es el detail", detail)
    //     setProductDetail(detail.data)

    // }, [])


    const categoria = useParams().category
    const [category, setCategory] = useState()

    useEffect(async() => {
        const respuesta = service.get(`/product/category/${categoria}`)
        setCategory(respuesta)

    }, [])




    return (
        <>
        {
            category === undefined?(<img style={{flex:'display', justifyContent:'center', alignItems:'center'  }} style={{width:70}} src="https://res.cloudinary.com/dannblackfish/image/upload/v1615395373/deadstarclothing/gif/Logo-DS_a7t7bj.gif"/>):(
                category.map((e,id)=>{
                    return(
                        <>

    <div class="bg-white">
        <div class="mx-auto py-12 px-4 max-w-7xl sm:px-6 lg:px-8 lg:py-24">
            <div class="space-y-12">
            <div class="space-y-5 sm:space-y-4 md:max-w-xl lg:max-w-3xl xl:max-w-none">
                <h2 class="text-3xl font-extrabold tracking-tight sm:text-4xl">STORE</h2>
                <p class="text-xl text-gray-500">Odio nisi, lectus dis nulla. Ultrices maecenas vitae rutrum dolor ultricies donec risus sodales. Tempus quis et.</p>
            </div>
            <ul class="space-y-12 sm:grid sm:grid-cols-2 sm:gap-x-6 sm:gap-y-12 sm:space-y-0 lg:grid-cols-3 lg:gap-x-8">

{/* <Link to="/category/Shirts"></Link> */}
                <Link to={`/category/`}>
                <li>
                <div class="space-y-4">
                    <div class="aspect-w-3 aspect-h-2">
                    <img class="object-cover shadow-lg rounded-lg" src="https://res.cloudinary.com/dannblackfish/image/upload/v1614902346/deadstarclothing/product-image/_DSC0001_zy6gao.jpg" alt="" />
                    </div>

                    <div class="space-y-2">
                    <div class="text-lg leading-6 font-medium space-y-1">
                        <h3>Join th cult - pink</h3>
                        <p class="text-indigo-600">$16.50</p>
                    </div>
                    </div>
                </div>
                </li>
                </Link>

            </ul>
            </div>
        </div>
    </div>
                    </>
                )
            })
        )
    }
    </>
    )
}
